          // Extract fields from Webhook payload
          const body = $json.body || {};
          const query = $json.query || {};
          const headers = ($json.headers || {});

          // Candidates for destination from Twilio request
          const candidates = [
            body.To,
            body.Called,
            query.To,
            query.Called,
            headers['x-target-number'],
            body['SipHeader_X-Target-Number'],
            body['SipHeader_X_Target_Number'],
            body['SipHeader_Target'],
            body.to,
            body.to_number
          ].filter(Boolean);

          function extractE164(s) {
            if (!s) return null;
            let str = String(s);
            try { str = decodeURIComponent(str); } catch (e) {}
            const m = str.match(/\+?\d{6,15}/);
            if (!m) return null;
            const n = m[0];
            return n.startsWith('+') ? n : '+' + n;
          }

          let dest = null;
          for (const c of candidates) {
            dest = extractE164(c);
            if (dest) break;
          }

          // Configure your WhatsApp sender here or via env var WA_SENDER
          const WA_SENDER = $env.WA_SENDER || 'whatsapp:+16722022556';

          let twiml;
          if (!dest) {
            twiml = `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Say voice="alice">Destination number missing.</Say>
</Response>`;
          } else {
            twiml = `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Dial callerId="${WA_SENDER}">
    <WhatsApp>${dest}</WhatsApp>
  </Dial>
</Response>`;
          }

          return [{ json: { twiml, dest, received: { body, query, headers } } }];

