// Get the first email object from the trigger array
const emailArray = $input.all();
if (!emailArray.length) throw new Error('No email data received');
const email = emailArray[0].json;

// Helper to collect all body parts recursively
function collectBodies(parts) {
  let bodies = [];
  for (const part of parts) {
    if (part.mimeType === 'text/plain' && part.body?.data) {
      bodies.push(Buffer.from(part.body.data, 'base64').toString('utf-8'));
    }
    if (part.mimeType === 'text/html' && part.body?.data) {
      // Optionally strip HTML tags here, or leave for the AI
      bodies.push(Buffer.from(part.body.data, 'base64').toString('utf-8'));
    }
    if (part.parts) {
      bodies = bodies.concat(collectBodies(part.parts));
    }
  }
  return bodies;
}

// Build the full body string
let body = '';
if (email.payload?.parts) {
  const parts = collectBodies(email.payload.parts);
  body = parts.join('\n\n').trim();
}
