// Process and categorize trending topics
const googleTrends = $('Fetch Google Trends').item?.json || {};
const twitterTrends = $('Fetch Twitter/X Trends').item?.json || {};

// Default trending data (fallback)
const defaultTrends = [
  {
    rank: 1,
    category: 'Lifestyle',
    title: 'Winter Wellness',
    description: 'Self-care and wellness content is trending! Share your morning routines, skincare tips, and mental health practices.',
    tags: ['#wellness', '#selfcare', '#winterwellness', '#mindfulness']
  },
  {
    rank: 2,
    category: 'Business',
    title: 'AI & Automation',
    description: 'Content about AI tools and automation is getting massive engagement. Share your productivity tips!',
    tags: ['#AI', '#automation', '#productivity', '#tech']
  },
  {
    rank: 3,
    category: 'Fashion',
    title: 'Capsule Wardrobe',
    description: 'Minimalist fashion and versatile pieces are resonating with audiences seeking sustainable style.',
    tags: ['#capsulewardrobe', '#minimalistfashion', '#sustainable', '#ootd']
  },
  {
    rank: 4,
    category: 'Food',
    title: 'Healthy Meal Prep',
    description: 'Easy meal prep content continues to see high engagement. Share your weekly prep routines!',
    tags: ['#mealprep', '#healthyeating', '#nutrition', '#foodie']
  },
  {
    rank: 5,
    category: 'Fitness',
    title: 'Micro Workouts',
    description: 'Quick 5-10 minute workout content is performing exceptionally well for busy audiences.',
    tags: ['#microworkout', '#quickworkout', '#fitness', '#homefitness']
  },
  {
    rank: 6,
    category: 'Creative',
    title: 'Behind The Scenes',
    description: 'Authentic BTS content showing your process is building strong connections with followers.',
    tags: ['#bts', '#behindthescenes', '#process', '#authentic']
  }
];

// Try to parse real trends if available, otherwise use defaults
let trends = defaultTrends;

try {
  if (googleTrends && typeof googleTrends === 'string') {
    // Parse RSS feed if it's XML
    const items = googleTrends.match(/<item>([\s\S]*?)<\/item>/g) || [];
    if (items.length > 0) {
      trends = items.slice(0, 6).map((item, index) => {
        const title = item.match(/<title>([^<]+)<\/title>/)?.[1] || `Trend ${index + 1}`;
        return {
          rank: index + 1,
          category: 'Trending',
          title: title,
          description: `This topic is currently trending on Google. Create content around "${title}" for maximum visibility.`,
          tags: [`#${title.replace(/\s+/g, '').toLowerCase()}`, '#trending', '#viral', '#explore']
        };
      });
    }
  }
} catch (e) {
  console.log('Using default trends due to parsing error');
}

return [{ json: { trends } }];

