try {
  const response = $input.first().json;
  const enhanced = JSON.parse(response.choices[0].message.content);
  return [{ json: enhanced }];
} catch (e) {
  // Return original trends if AI enhancement fails
  return [{ json: $('Process Trending Data').item.json }];
