// Extract and validate input parameters
const body = $input.first().json.body || $input.first().json;

const payload = {
  prompt: body.prompt || '',
  provider: body.provider || 'openai',
  contentType: body.contentType || 'post',
  brand: body.brand || {},
  hashtagStrategy: body.hashtagStrategy || 'auto',
  bulkMode: body.bulkMode || false,
  variationCount: body.variationCount || 1,
  referenceImages: body.referenceImages || [],
  apiKey: body.apiKey || ''
};

// Validate required fields
if (!payload.prompt) {
  return [{ json: { error: 'Prompt is required', success: false } }];
}

if (!payload.apiKey) {
  return [{ json: { error: 'API key is required', success: false } }];
}

return [{ json: payload }];

// Build OpenAI request body as proper JSON
const input = $input.first().json;

const brandName = input.brand?.name || 'General';
const brandDescription = input.brand?.description || 'Professional and engaging';
const brandAudience = input.brand?.audience || 'General audience';
const brandTags = input.brand?.tags ? input.brand.tags.join(', ') : 'modern, trendy';

const systemPrompt = `You are an expert Instagram content creator and social media strategist. Your job is to create viral, engaging content that drives maximum engagement.

Brand: ${brandName}
Brand Voice: ${brandDescription}
Target Audience: ${brandAudience}
Style Tags: ${brandTags}

Create content that:
- Is scroll-stopping and attention-grabbing
- Uses emojis naturally and effectively
- Includes a clear call-to-action
- Matches the brand voice perfectly
- Is optimized for the Instagram algorithm`;

const userPrompt = `Create an engaging Instagram ${input.contentType} for: ${input.prompt}

Provide your response in this exact JSON format:
{
  "caption": "The main caption text with emojis",
  "hashtags": ["hashtag1", "hashtag2"],
  "imagePrompt": "Detailed prompt for image generation",
  "hook": "The attention-grabbing first line",
  "cta": "Call to action text"
}`;

const requestBody = {
  model: 'gpt-4-turbo-preview',
  messages: [
    { role: 'system', content: systemPrompt },
    { role: 'user', content: userPrompt }
  ],
  max_tokens: 1000,
  response_format: { type: 'json_object' }
};

return [{ json: { requestBody, apiKey: input.apiKey, originalInput: input } }];

// Build Gemini request body as proper JSON
const input = $input.first().json;

const brandName = input.brand?.name || 'General';
const brandDescription = input.brand?.description || 'Professional and engaging';
const brandAudience = input.brand?.audience || 'General audience';
const brandTags = input.brand?.tags ? input.brand.tags.join(', ') : 'modern, trendy';

const prompt = `You are an expert Instagram content creator. Create viral, engaging content.

Brand: ${brandName}
Brand Voice: ${brandDescription}
Target Audience: ${brandAudience}
Style Tags: ${brandTags}

Create an Instagram ${input.contentType} for: ${input.prompt}

Respond ONLY with valid JSON in this exact format:
{
  "caption": "The main caption with emojis",
  "hashtags": ["hashtag1", "hashtag2"],
  "imagePrompt": "Detailed image prompt",
  "hook": "First attention-grabbing line",
  "cta": "Call to action"
}`;

const requestBody = {
  contents: [{
    parts: [{ text: prompt }]
  }],
  generationConfig: {
    temperature: 0.9,
    maxOutputTokens: 1000
  }
};

return [{ json: { requestBody, apiKey: input.apiKey, originalInput: input } }];

// Parse OpenAI response
const response = $input.first().json;
const originalInput = $('Build OpenAI Request').item.json.originalInput;

try {
  const content = JSON.parse(response.choices[0].message.content);
  return [{ json: { success: true, content, originalInput } }];
} catch (e) {
  return [{ json: { success: false, error: 'Failed to parse OpenAI response: ' + e.message, originalInput } }];
}

// Parse Gemini response
const response = $input.first().json;
const originalInput = $('Build Gemini Request').item.json.originalInput;

try {
  const text = response.candidates[0].content.parts[0].text;
  let content;

  // Try to parse directly
  try {
    content = JSON.parse(text);
  } catch (e) {
    // Try to extract JSON from markdown code block
    const jsonMatch = text.match(/```json\n([\s\S]*?)\n```/) || text.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      content = JSON.parse(jsonMatch[1] || jsonMatch[0]);
    } else {
      throw new Error('Could not extract JSON from response');
    }
  }

  return [{ json: { success: true, content, originalInput } }];
} catch (e) {
  return [{ json: { success: false, error: 'Failed to parse Gemini response: ' + e.message, originalInput } }];
}

// Merge results from either provider
const items = $input.all();
const result = items[0].json;

if (!result.success) {
  return [{ json: result }];
}

// Format final response
const content = result.content;
const originalInput = result.originalInput;

const formattedResult = {
  success: true,
  content: {
    caption: content.caption,
    hashtags: (content.hashtags || []).map(h => h.startsWith('#') ? h : '#' + h),
    imagePrompt: content.imagePrompt,
    hook: content.hook,
    cta: content.cta,
    contentType: originalInput.contentType
  }
};

return [{ json: formattedResult }];

