// === Parse email received date ===
const emailReceivedStr = $('Notification received').first().json.date; // e.g., "Tue, 28 Oct 2025 22:34:56 +0200"
const emailReceived = new Date(emailReceivedStr);

// === Parse meeting date and time from Output Parser ===
const meetingDateStr = $input.first().json.output.meeting_date; // e.g., "2025-10-29"
const meetingTimeStr = $input.first().json.output.meeting_time; // e.g., "16:00"

// === Combine meeting date and time with EET offset (+02:00) ===
const meetingDateTime = new Date(`${meetingDateStr}T${meetingTimeStr}:00+02:00`);

// === Calculate difference in hours between meeting and email received ===
const diffHrs = (meetingDateTime - emailReceived) / (1000 * 60 * 60);

// === Prepare wait times for reminders ===
const waitOutput = {
    wait_24h: 0, // Reminder 24h before meeting
    wait_1h: 0   // Reminder 1h before meeting
};

// If the difference is more than 24h, calculate 24h reminder
if (diffHrs > 24) {
    waitOutput.wait_24h = Math.floor(diffHrs - 24);
}

// If the difference is less than 24h, calculate 1h reminder
if (diffHrs < 24) {
    waitOutput.wait_1h = Math.floor(diffHrs - 1);
}

// Ensure both fields are numbers (null replaced with 0)
waitOutput.wait_24h = waitOutput.wait_24h || 0;
waitOutput.wait_1h = waitOutput.wait_1h || 0;

// === Return JSON object ===
return [
    {
        json: waitOutput
    }
];
